import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Grid,
  Hidden,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
} from '@mui/material';
import { Link } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import styles from './Header.module.css';

const Header = () => {
  const [mobileOpen, setMobileOpen] = useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <List>
        <ListItem button component={Link} to="/products" onClick={handleDrawerToggle}>
          <ListItemText primary="Products" />
        </ListItem>
        <ListItem button component={Link} to="/about-us" onClick={handleDrawerToggle}>
          <ListItemText primary="About Us" />
        </ListItem>
        <ListItem button component={Link} to="/cart" onClick={handleDrawerToggle}>
          <ListItemText primary="Cart" />
        </ListItem>
        <ListItem button component={Link} to="/account" onClick={handleDrawerToggle}>
          <ListItemText primary="Account" />
        </ListItem>
      </List>
    </div>
  );

  return (
    <AppBar position="static">
      <Toolbar>
        <Grid container alignItems="center">
          <Grid item xs={10} sm={4}>
            <Typography variant="h6" component={Link} to="/" className={styles.brandName}>
              Clothing Store
            </Typography>
          </Grid>
          <Hidden xsDown>
            <Grid item sm={4} container justifyContent="center">
              <Button component={Link} to="/products" color="inherit">
                Products
              </Button>
              <Button component={Link} to="/about-us" color="inherit">
                About Us
              </Button>
            </Grid>
            <Grid item sm={4} container justifyContent="flex-end">
              <Button component={Link} to="/cart" color="inherit">
                Cart
              </Button>
              <Button component={Link} to="/account" color="inherit">
                Account
              </Button>
            </Grid>
          </Hidden>
          <Hidden smUp>
            <Grid item xs={2} container justifyContent="flex-end">
              <IconButton color="inherit" edge="end" onClick={handleDrawerToggle}>
                <MenuIcon />
              </IconButton>
            </Grid>
          </Hidden>
        </Grid>
      </Toolbar>
      <Hidden smUp>
        <Drawer
          anchor="right"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
        >
          {drawer}
        </Drawer>
      </Hidden>
    </AppBar>
  );
};

export default Header;
