import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Drawer, Box } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { Link } from 'react-router-dom';
import ListItemButton from '@mui/material/ListItemButton';
import { List, ListItemText } from '@mui/material';
import styles from './Header.module.css';

const Header = () => {
  const [mobileOpen, setMobileOpen] = useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div className={styles.navLinks}>
      <List>
        <ListItemButton component={Link} to="/our-dolls" onClick={handleDrawerToggle}>
          <ListItemText primary="Our Dolls" />
        </ListItemButton>
        <ListItemButton component={Link} to="/new-items" onClick={handleDrawerToggle}>
          <ListItemText primary="New Items" />
        </ListItemButton>
        <ListItemButton component={Link} to="/about-us" onClick={handleDrawerToggle}>
          <ListItemText primary="About Us" />
        </ListItemButton>
        <ListItemButton component={Link} to="/contacts" onClick={handleDrawerToggle}>
          <ListItemText primary="Contacts" />
        </ListItemButton>
      </List>
    </div>
  );

  return (
    <AppBar position="static" color="default">
      <Toolbar>
        <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center' }}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleDrawerToggle}
            sx={{ display: { xs: 'block', md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          <Drawer anchor="left" open={mobileOpen} onClose={handleDrawerToggle}>
            {drawer}
          </Drawer>
          <Typography variant="h2" className={styles.pageName}>
            Olalka
          </Typography>
          <Box className={styles.desktopNav} sx={{ display: { xs: 'none', md: 'block' } }}>
            {drawer}
          </Box>
        </Box>
        <Box>
          <IconButton color="inherit">
            <SearchIcon />
          </IconButton>
          <IconButton component={Link} to="/cart" color="inherit">
            <ShoppingCartIcon />
          </IconButton>
          <IconButton component={Link} to="/account" color="inherit">
            <AccountCircleIcon />
          </IconButton>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
